function [] = display_question(w,col,instruct,txtloc)% [] = display_question(w,col,instruct,txtloc)%% W = the window you pass into SCREEN%% COL = color, e.g. WHITE_INDEX%% INSTRUCT = 1 for 'longer' and 2 for 'shorter'%% TXTLOC = x and y location of the text% if you change these questionsif instruct == 1		SCREEN(w,'TextSize',16);	txt = 'Which side is longer?';elseif instruct == 2	SCREEN(w,'TextSize',16);	txt = 'Which side is shorter?';	end[x y] = SCREEN(w, 'DrawText', txt, txtloc(1), txtloc(2), col);